import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function StudentDetail() {
  const { studentId } = useParams();
  const [detail, setDetail] = useState();
  useEffect(() => {
    const uri = "http://127.0.0.1:8000/api/student-detail/" + studentId;
    axios.get(uri).then((res) => {
      console.log(res.data, "res test");
      setDetail(res.data);
    });
  }, []);

  return (
    <>
      <div className="container mx-auto">
        <div className="relative overflow-x-auto shadow-md sm:rounded-lg">
          <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400">
            <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
              <tr>
                <th scope="col" className="px-6 py-3">
                  Ders
                </th>
                <th scope="col" className="px-6 py-3">
                  <span className="sr-only">Notlara Git</span>
                </th>
              </tr>
            </thead>
            <tbody>
              {detail.length > 0 ? detail.subjects.map((subject) => (
                   <tr className="bg-white border-b dark:bg-gray-800 dark:border-gray-700">
                   <th scope="row" className="px-6 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
                        {subject.title}
                   </th>
                   <td className="px-6 py-4 text-right">
                       <a href={'/rate-view/'+subject.id+'/'+studentId} className="font-medium text-blue-600 dark:text-blue-500 hover:underline">Ä°ncele</a>
                   </td>
               </tr>
)):<div>no data</div>}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}

export default StudentDetail;
